import React, { Component } from 'react';

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from "@material-ui/core/Button";
import { Menu, Dropdown } from 'antd';


import { slide as SlideMenu } from 'react-burger-menu';

// import SideDrawer from './SideDrawer';


const menu = (
  <Menu>
    <Menu.Item>
      <a target="_blank" rel="noopener noreferrer" href="/orders">
        Orders
      </a>
    </Menu.Item>
    <Menu.Item>
      <a target="_blank" rel="noopener noreferrer" href="/contact">
        Contact us
      </a>
    </Menu.Item>
    <Menu.Item>
      <a target="_blank" rel="noopener noreferrer" href="#">
        Logout
      </a>
    </Menu.Item>
  </Menu>
);

const UserDropDown = () => (
  <div className="user-dropdown">
    <Dropdown overlay={menu}>
      <a className="user" href="#">
        user
      </a>
    </Dropdown>
  </div>
);

class Header extends Component {

    state = {
        drawerOpen: false,
        headerShow: false,
        menu: false
    }

    componentDidMount(){
        window.addEventListener('scroll',this.handleScroll);
    }


    handleScroll = () => {
       if(window.scrollY > 0){
            this.setState({
                headerShow: true
            })
       } else {
            this.setState({
                headerShow: false
            })
       }
    }


    toggleDrawer = (value) => {
        this.setState({
            drawerOpen: value
        })
    }

    toggleMenu = () => {
      this.setState({
        menu: !this.state.menu
      });
    };

    render() {
        return (
            <AppBar
                position="fixed"
                style={{
                    backgroundColor: this.state.headerShow ? '#966c4c' : 'transparent',
                    boxShadow: 'none',
                    padding: '5px 0px',
                }}
            >
                <Toolbar>

                    <div className="logo">
                      <a href="/">
                        <img src="/images/welcome_logo.png"/>
                      </a>
                    </div>

                    <div className="top-menu">

                          <a href="/" className="header_btn"> Home</a>            
                          <a href="/packages" className="header_btn">Packages </a>         
                          <a href="/contact" className="header_btn"> Contact us </a>

                    </div>

                    <UserDropDown/>

                </Toolbar>
            </AppBar>
        );
    }
}

export default Header;


